using SGAL.AcessoDados;
using SGAL.Dominio;
using SGAL.Web.Models;
using System;
using System.Collections.Generic;
using System.Drawing;
using System.Drawing.Imaging;
using System.IO;
using System.Linq;
using System.Web;
using System.Web.Mvc;

namespace SGAL.Web.Controllers
{
    public class PecaController : Controller
    {
        private SGALContext sgalContext;
        private RepositorioPeca repositorioPeca;

        public PecaController()
        {
            sgalContext = new SGALContext();
            repositorioPeca = new RepositorioPeca(sgalContext);
        }

        protected override void Dispose(bool disposing)
        {
            repositorioPeca.Dispose();
            sgalContext.Dispose();
            base.Dispose(disposing);
        }

        public ActionResult Index()
        {
            var pecas = from l in repositorioPeca.Recuperar()
                        orderby l.Descricao
                        select l;

            return View(pecas);
        }

        [HttpGet]
        public ActionResult Edit(int idPeca)
        {
            var pecaParaEditar = repositorioPeca.Recuperar(idPeca);
            return View(new PecaView(pecaParaEditar.PecaID) { Descricao = pecaParaEditar.Descricao });
        }

        [HttpPost]
        public ActionResult Edit(int idPeca, PecaView pecaView)
        {
            var pecaParaEditar = repositorioPeca.Recuperar(idPeca);
            pecaParaEditar.Descricao = pecaView.Descricao;
            if (pecaView.Imagem != null)
            {
                var binaryReader = new BinaryReader(pecaView.Imagem.InputStream);
                pecaParaEditar.ByteArrayImage = binaryReader.ReadBytes(pecaView.Imagem.ContentLength);
            }

            repositorioPeca.Atualizar(pecaParaEditar);
            repositorioPeca.Salvar();

            return RedirectToAction("Index");
        }

        [HttpGet]
        public ActionResult Create()
        {
            var peca = new PecaView();
            return View(peca);
        }

        [HttpPost]
        public ActionResult Create(PecaView pecaView)
        {
            if (ModelState.IsValid)
            {
                var binaryReader = new BinaryReader(pecaView.Imagem.InputStream);

                var peca = new Peca();
                peca.Descricao = pecaView.Descricao;
                peca.ByteArrayImage = binaryReader.ReadBytes(pecaView.Imagem.ContentLength);

                repositorioPeca.Inserir(peca);
                repositorioPeca.Salvar();

                return RedirectToAction("Index");
            }
            return View();
        }

